function beforeSwap(address sender, PoolKey calldata key, IPoolManager.SwapParams calldata params, bytes calldata hookData) external override returns (bytes4) {
    (bytes memory sig, uint256 nonce, uint256 expiryTime) = abi.decode(hookData, (bytes, uint256, uint256));

    bytes32 swapDigest = keccak256(
        abi.encode(
            sender,  // Only checks router address, not original caller
            key,
            params.zeroForOne,
            params.amountSpecified,
            params.sqrtPriceLimitX96,
            nonce,
            expiryTime
        )
    );

    // sender = router.address, validates the router only
    require(SignatureChecker.isValidSignatureNow(quoteSigner, swapDigest, sig), "Invalid signature");
    _useUnorderedNonce(nonce);  // Marks nonce as consumed
    return IHooks.beforeSwap.selector;
}

// Public router accepts any caller
interface IRouter {
    function swap(PoolKey calldata key, IPoolManager.SwapParams calldata params, bytes calldata hookData) external;
}

