// FIXED: Access control added
function getCompoundInterestRateAndUpdate(
    uint256 _collateralAssets,
    uint256 _debtAssets,
    uint256 _interestRateTimestamp
)
    external
    virtual
    returns (uint256 rcomp)
{
    // FIX: Verify caller is the Silo
    require(msg.sender == modelState.silo, InvalidSilo());

    (rcomp, modelState.k) = _getCompoundInterestRate(CompoundInterestRateArgs({
        silo: modelState.silo,
        collateralAssets: _collateralAssets,
        debtAssets: _debtAssets,
        interestRateTimestamp: _interestRateTimestamp,
        blockTimestamp: block.timestamp,
        usePending: false
    }));
}

// RESULT:
// - Only the Silo can call this function
// - k can no longer be arbitrarily manipulated
// - Interest rates are determined by legitimate market utilization
// - Protocol stability maintained

// FIXED: Defer k update until activation
function setConfig(IDynamicKinkModel.Config calldata _config) 
    external 
    onlyOwner 
{
    IRM.verifyConfig(_config);

    // Schedule configuration without modifying state
    pendingConfig = _config;
    pendingConfigActivationTime = block.timestamp + TIMELOCK_DURATION;

    // DO NOT update k here - it will be updated during activation
    // modelState.k remains unchanged throughout the timelock period

    emit ConfigUpdateScheduled(_config, pendingConfigActivationTime);
}

function activateConfig() external {
    require(block.timestamp >= pendingConfigActivationTime, "Timelock not elapsed");
    require(pendingConfig != address(0), "No pending config");

    // NOW apply k change when configuration officially activates
    activeConfig = pendingConfig;
    modelState.k = pendingConfig.kmin;  // k updated here, not in setConfig()
    pendingConfig = address(0);

    emit ConfigActivated(activeConfig);
}

function cancelPendingConfig() external onlyOwner {
    // Clean cancellation - k was never changed, so nothing to restore
    pendingConfig = address(0);
    pendingConfigActivationTime = 0;

    emit ConfigCancelled();
}

// RESULT:
// - setConfig() is now a pure state scheduling operation
// - k remains stable throughout timelock period
// - activateConfig() applies all changes atomically
// - cancelPendingConfig() is truly a no-op
