 */
pragma solidity 0.8.22;

import { AccountErrors } from

;

/**
 * @title Arcadia Spot Account
 * @author Pragma Labs
 * @notice Arcadia Spot Accounts enables individuals, DAOs, and other protocols to deposit and manage a variety of assets easily through Asset Managers.
 * Asset Managers are selected by Spot Account holders and can facilitate automation for tasks such as Liquidity Management and Compounding, among others.
 */
contract AccountSpot is AccountStorageV1, IAccount {
    using SafeTransferLib for ERC20;

    /* //////////////////////////////////////////////////////////////
                                CONSTANTS
    ////////////////////////////////////////////////////////////// */

    // The current Account Version.
    uint256 public constant ACCOUNT_VERSION = 2;
    // The cool-down period after an account action, that might be disadvantageous for a new Owner,
    // during which ownership cannot be transferred to prevent the old Owner from frontrunning a transferFrom().
    uint256 public constant COOL_DOWN_PERIOD = 5 minutes;
    // Storage slot with the address of the current implementation.
    // This is the hardcoded keccak-256 hash of:

subtracted by 1.
   bytes32 internal constant IMPLEMENTATION_SLOT = 0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc;
   // The contract address of the Arcadia Accounts Factory.
   address public immutable FACTORY;
   // Uniswap Permit2 contract
   IPermit2 internal immutable PERMIT2 = IPermit2(0x000000000022D473030F116dDEE9F6B43aC78BA3);

   // Storage slot for the Account implementation, a struct to avoid storage conflict when dealing with upgradeable contracts.
   struct AddressSlot {
       address value;
   }

   /* //////////////////////////////////////////////////////////////
                               EVENTS
   ////////////////////////////////////////////////////////////// */

   event AssetManagerSet(address indexed owner, address indexed assetManager, bool value);

   /* //////////////////////////////////////////////////////////////
                               MODIFIERS
   ////////////////////////////////////////////////////////////// */

   /**
    * @dev Throws if function is reentered.
    */
   modifier nonReentrant() {
       if (locked != 1) revert AccountErrors.NoReentry();
       locked = 2;
       _;
       locked = 1;
   }

   /**
    * @dev Throws if called by any address other than an Asset Manager or the owner.
    */
   modifier onlyAssetManager() {
       // A custom error would need to read out owner + isAssetManager storage
       require(msg.sender == owner || isAssetManager[owner][msg.sender],

);
        _;
    }

    /**
     * @dev Throws if called by any address other than the Factory address.
     */
    modifier onlyFactory() {
        if (msg.sender != FACTORY) revert AccountErrors.OnlyFactory();
        _;
    }

    /**
     * @dev Throws if called by any address other than the owner.
     */
    modifier onlyOwner() {
        if (msg.sender != owner) revert AccountErrors.OnlyOwner();
        _;
    }

    /**
     * @dev Starts the cool-down period during which ownership cannot be transferred.
     * This prevents the old Owner from frontrunning a transferFrom().
     */
    modifier updateActionTimestamp() {
        lastActionTimestamp = uint32(block.timestamp);
        _;
    }

    /* //////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    ////////////////////////////////////////////////////////////// */

    /**
     * @param factory The contract address of the Arcadia Accounts Factory.
     */
    constructor(address factory) {
        // This will only be the owner of the Account implementation.
        // and will not affect any subsequent proxy implementation using this Account implementation.
        owner = msg.sender;

        FACTORY = factory;
    }

    /* ///////////////////////////////////////////////////////////////
                          ACCOUNT MANAGEMENT
    /////////////////////////////////////////////////////////////// */

    /**
     * @notice Initiates the variables of the Account.
     * @param owner_ The sender of the 'createAccount' on the Factory
     * @param registry_ The 'beacon' contract with the external logic to price assets.
     * @dev A proxy will be used to interact with the Account implementation.
     * This function will only be called (once) in the same transaction as the proxy Account creation through the Factory.
     * @dev initialize has implicitly a nonReentrant guard, since the

variable has value zero until the end of the function.
    * @dev The Registry is not used in spot accounts, but a valid registry must be set to be compatible with V1 Accounts.
    */
   function initialize(address owner_, address registry_, address) external onlyFactory {
       if (registry_ == address(0)) revert AccountErrors.InvalidRegistry();
       owner = owner_;
       registry = registry_;

       locked = 1;
   }

   /**
    * @notice Upgrades the Account version and stores a new address in the EIP1967 implementation slot.
    * @param newImplementation The new contract address of the Account implementation.
    * @param newRegistry The Registry for this specific newImplementation.
    * @param data Arbitrary data, can contain instructions to execute when updating Account to new implementation.
    * @dev This function MUST be added to new Account implementations.
    */
   function upgradeAccount(address newImplementation, address newRegistry, uint256, bytes calldata data)
       external
       onlyFactory
       nonReentrant
       updateActionTimestamp
   {
       // Cache old parameters.
       address oldImplementation = _getAddressSlot(IMPLEMENTATION_SLOT).value;
       uint256 oldVersion = ACCOUNT_VERSION;

       // Store new parameters.
       _getAddressSlot(IMPLEMENTATION_SLOT).value = newImplementation;
       registry = newRegistry;

       // Hook on the new logic to finalize upgrade.
       // Used to eg. Remove exposure from old Registry and add exposure to the new Registry.
       // Extra data can be added by the Factory for complex instructions.
       this.upgradeHook(oldImplementation, address(0), oldVersion, data);

       // Event emitted by Factory.
   }

   /**
    * @notice Returns the

.
     * @param slot The slot where the address of the Logic contract is stored.
     * @return r The address stored in slot.
     */
    function _getAddressSlot(bytes32 slot) internal pure returns (AddressSlot storage r) {
        assembly {
            r.slot := slot
        }
    }

    /**
     * @notice Finalizes the Upgrade from a different Account version to this version.
     * param oldImplementation The old contract address of the Account implementation.
     * param oldRegistry The Registry of the old version (might be identical to the new registry)
     * param oldVersion The old version of the Account implementation.
     * param data Arbitrary data, can contain instructions to execute in this function.
     * @dev If upgradeHook() is implemented, it MUST verify that msg.sender == address(this).
     * @dev We delete the deprecated AccountStorageV1 variables.
     */
    function upgradeHook(address, address, uint256, bytes calldata) external {
        if (msg.sender != address(this)) revert AccountErrors.OnlySelf();
        if (registry == address(0)) revert AccountErrors.InvalidRegistry();

        // Require that no creditor is set and no auctions are ongoing.
        // (This should always be enforced in the old Version we upgrade from, but we do a redundant safety check).
        if (creditor != address(0) || inAuction) revert AccountErrors.InvalidUpgrade();

        // Delete margin account related storage data (should normally already be empty).
        delete liquidator;
        delete minimumMargin;
        delete numeraire;

        // Delete asset related storage data.
        uint256 erc20StoredLength = erc20Stored.length;
        for (uint256 i = 0; i < erc20StoredLength; ++i) {
            delete erc20Balances[erc20Stored[i]];
        }
        delete erc20Stored;

        delete erc721Stored;
        delete erc721TokenIds;

        uint256 erc1155StoredLength = erc1155Stored.length;
        for (uint256 j = 0; j < erc1155StoredLength; ++j) {
            delete erc1155Balances[erc1155Stored[j]][erc1155TokenIds[j]];
        }
        delete erc1155Stored;
        delete erc1155TokenIds;
    }

    /* ///////////////////////////////////////////////////////////////
                        OWNERSHIP MANAGEMENT
    /////////////////////////////////////////////////////////////// */

    /**
     * @notice Transfers ownership of the contract to a new Account.
     * @param newOwner The new owner of the Account.
     * @dev Can only be called by the current owner via the Factory.
     * A transfer of ownership of the Account is triggered by a transfer
     * of ownership of the accompanying ERC721 Account NFT, issued by the Factory.
     * Owner of Account NFT = owner of Account
     * @dev Function uses a cool-down period during which ownership cannot be transferred.
     * Cool-down period is triggered after any account action, that might be disadvantageous for a new Owner.
     * This prevents the old Owner from frontrunning a transferFrom().
     */
    function transferOwnership(address newOwner) external onlyFactory {
        if (block.timestamp <= lastActionTimestamp + COOL_DOWN_PERIOD) revert AccountErrors.CoolDownPeriodNotPassed();

        // The Factory will check that the new owner is not address(0).
        owner = newOwner;
    }

    /*///////////////////////////////////////////////////////////////
                       ASSET MANAGER ACTIONS
    ///////////////////////////////////////////////////////////////*/

    /**
     * @notice Add or remove an Asset Manager.
     * @param assetManager The address of the Asset Manager.
     * @param value A boolean giving permissions to or taking permissions from an Asset Manager.
     * @dev Only set trusted addresses as Asset Manager. Asset Managers have full control over assets in the Account.
     * @dev No need to set the Owner as Asset Manager as they will automatically have all permissions of an Asset Manager.
     * @dev Potential use-cases of the Asset Manager might be to:
     * - Liquidity Management.
     * - Do flash actions (optimistic actions).
     * - Compounding.
     * - Chain multiple interactions together.
     * @dev Anyone can set the Asset Manager for themselves, this will not impact the current owner of the Account
     * since the combination of

is used in authentication checks.
    * This guarantees that when the ownership of the Account is transferred, the asset managers of the old owner have no
    * impact on the new owner. But the new owner can still remove any existing asset managers before the transfer.
    */
   function setAssetManager(address assetManager, bool value) external {
       emit AssetManagerSet(msg.sender, assetManager, isAssetManager[msg.sender][assetManager] = value);
   }

   /**
    * @notice Executes a flash action.
    * @param actionTarget The contract address of the actionTarget to execute external logic.
    * @param actionData A bytes object containing three structs and two bytes objects.
    * The first struct contains the info about the assets to withdraw from this Account to the actionTarget.
    * The second struct contains the info about the owner's assets that need to be transferred from the owner to the actionTarget.
    * The third struct contains the permit for the Permit2 transfer.
    * The first bytes object contains the signature for the Permit2 transfer.
    * The second bytes object contains the encoded input for the actionTarget.
    * @dev This function optimistically chains multiple actions together (= do a flash action):
    * - It can optimistically withdraw assets from the Account to the actionTarget.
    * - It can transfer assets directly from the owner to the actionTarget.
    * - It can execute external logic on the actionTarget, and interact with any DeFi protocol to swap, stake, claim...
    * - It can deposit all recipient tokens from the actionTarget back into the Account.
    */
   function flashAction(address actionTarget, bytes calldata actionData)
       external
       payable
       onlyAssetManager
       nonReentrant
       updateActionTimestamp
   {
       // Decode flash action data.
       (
           ActionData memory withdrawData,
           ActionData memory transferFromOwnerData,
           IPermit2.PermitBatchTransferFrom memory permit,
           bytes memory signature,
           bytes memory actionTargetData
       ) = abi.decode(actionData, (ActionData, ActionData, IPermit2.PermitBatchTransferFrom, bytes, bytes));

       // Withdraw assets to the actionTarget.
       _withdraw(
           withdrawData.assets, withdrawData.assetIds, withdrawData.assetAmounts, withdrawData.assetTypes, actionTarget
       );

       // Transfer assets from owner (that are not assets in this account) to the actionTarget.
       if (transferFromOwnerData.assets.length > 0) {
           _transferFromOwner(transferFromOwnerData, actionTarget);
       }

       // If the function input includes a signature and non-empty token permissions,
       // initiate a transfer from the owner to the actionTarget via Permit2.
       if (signature.length > 0 && permit.permitted.length > 0) {
           _transferFromOwnerWithPermit(permit, signature, actionTarget);
       }

       // Execute external logic on the actionTarget.
       ActionData memory depositData = IActionBase(actionTarget).executeAction(actionTargetData);

       // Deposit assets from actionTarget into Account.
       _deposit(
           depositData.assets, depositData.assetIds, depositData.assetAmounts, depositData.assetTypes, actionTarget
       );
   }

   /*///////////////////////////////////////////////////////////////
                         ASSET MANAGEMENT
   ///////////////////////////////////////////////////////////////*/

   /**
    * @notice Deposits assets into the Account.
    * @param assetAddresses Array of the contract addresses of the assets.
    * @param assetIds Array of the IDs of the assets.
    * @param assetAmounts Array with the amounts of the assets.
    * @param assetTypes Array of the asset types.
    */
   function deposit(
       address[] memory assetAddresses,
       uint256[] memory assetIds,
       uint256[] memory assetAmounts,
       uint256[] memory assetTypes
   ) external payable onlyOwner nonReentrant {
       _deposit(assetAddresses, assetIds, assetAmounts, assetTypes, msg.sender);
   }

   /**
    * @notice Deposits assets into the Account.
    * @param assetAddresses Array of the contract addresses of the assets.
    * @param assetIds Array of the IDs of the assets.
    * @param assetAmounts Array with the amounts of the assets.
    * @param assetTypes Array of the asset types.
    * @param from The assets deposited into the Account will come from this address.
    */
   function _deposit(
       address[] memory assetAddresses,
       uint256[] memory assetIds,
       uint256[] memory assetAmounts,
       uint256[] memory assetTypes,
       address from
   ) internal {
       for (uint256 i; i < assetAddresses.length; ++i) {
           // Skip if amount is 0 to prevent transferring addresses that have 0 balance.
           if (assetAmounts[i] == 0) continue;

           if (assetTypes[i] == 1) {
               ERC20(assetAddresses[i]).safeTransferFrom(from, address(this), assetAmounts[i]);
           } else if (assetTypes[i] == 2) {
               IERC721(assetAddresses[i]).safeTransferFrom(from, address(this), assetIds[i]);
           } else if (assetTypes[i] == 3) {
               IERC1155(assetAddresses[i]).safeTransferFrom(from, address(this), assetIds[i], assetAmounts[i],

);
            } else {
                revert AccountErrors.UnknownAssetType();
            }
        }
    }

    /**
     * @notice Withdraws assets from the Account to the owner.
     * @param assetAddresses Array of the contract addresses of the assets.
     * @param assetIds Array of the IDs of the assets.
     * @param assetAmounts Array with the amounts of the assets.
     * @param assetTypes Array of the asset types.
     */
    function withdraw(
        address[] memory assetAddresses,
        uint256[] memory assetIds,
        uint256[] memory assetAmounts,
        uint256[] memory assetTypes
    ) public onlyOwner nonReentrant updateActionTimestamp {
        _withdraw(assetAddresses, assetIds, assetAmounts, assetTypes, msg.sender);
    }

    /**
     * @notice Withdraws assets from the Account.
     * @param assetAddresses Array of the contract addresses of the assets.
     * @param assetIds Array of the IDs of the assets.
     * @param assetAmounts Array with the amounts of the assets.
     * @param assetTypes Array of the asset types.
     * @param to The address to withdraw to.
     */
    function _withdraw(
        address[] memory assetAddresses,
        uint256[] memory assetIds,
        uint256[] memory assetAmounts,
        uint256[] memory assetTypes,
        address to
    ) internal {
        for (uint256 i; i < assetAddresses.length; ++i) {
            // Skip if amount is 0 to prevent transferring addresses that have 0 balance.
            if (assetAmounts[i] == 0) continue;

            if (assetAddresses[i] == address(0)) {
                (bool success, bytes memory result) = payable(msg.sender).call{ value: assetAmounts[i] }(

);
                require(success, string(result));
            } else if (assetTypes[i] == 1) {
                ERC20(assetAddresses[i]).safeTransfer(to, assetAmounts[i]);
            } else if (assetTypes[i] == 2) {
                IERC721(assetAddresses[i]).safeTransferFrom(address(this), to, assetIds[i]);
            } else if (assetTypes[i] == 3) {
                IERC1155(assetAddresses[i]).safeTransferFrom(address(this), to, assetIds[i], assetAmounts[i],

);
            } else {
                revert AccountErrors.UnknownAssetType();
            }
        }
    }

    /**
     * @notice Transfers assets directly from the owner to the actionTarget contract.
     * @param transferFromOwnerData A struct containing the info of all assets transferred from the owner that are not in this account.
     * @param to The address to withdraw to.
     */
    function _transferFromOwner(ActionData memory transferFromOwnerData, address to) internal {
        uint256 assetAddressesLength = transferFromOwnerData.assets.length;
        address owner_ = owner;
        for (uint256 i; i < assetAddressesLength; ++i) {
            // Skip if amount is 0 to prevent transferring 0 balances.
            if (transferFromOwnerData.assetAmounts[i] == 0) continue;

            if (transferFromOwnerData.assetTypes[i] == 1) {
                ERC20(transferFromOwnerData.assets[i]).safeTransferFrom(
                    owner_, to, transferFromOwnerData.assetAmounts[i]
                );
            } else if (transferFromOwnerData.assetTypes[i] == 2) {
                IERC721(transferFromOwnerData.assets[i]).safeTransferFrom(owner_, to, transferFromOwnerData.assetIds[i]);
            } else if (transferFromOwnerData.assetTypes[i] == 3) {
                IERC1155(transferFromOwnerData.assets[i]).safeTransferFrom(
                    owner_, to, transferFromOwnerData.assetIds[i], transferFromOwnerData.assetAmounts[i],

                );
            } else {
                revert AccountErrors.UnknownAssetType();
            }
        }
    }

    /**
     * @notice Transfers assets from the owner to the actionTarget contract via Permit2.
     * @param permit Data specifying the terms of the transfer.
     * @param signature The signature to verify.
     * @param to_ The address to withdraw to.
     */
    function _transferFromOwnerWithPermit(
        IPermit2.PermitBatchTransferFrom memory permit,
        bytes memory signature,
        address to_
    ) internal {
        uint256 tokenPermissionsLength = permit.permitted.length;
        IPermit2.SignatureTransferDetails[] memory transferDetails =
            new IPermit2.SignatureTransferDetails[](tokenPermissionsLength);

        for (uint256 i; i < tokenPermissionsLength; ++i) {
            transferDetails[i].to = to_;
            transferDetails[i].requestedAmount = permit.permitted[i].amount;
        }

        PERMIT2.permitTransferFrom(permit, transferDetails, owner, signature);
    }

    /* ///////////////////////////////////////////////////////////////
                        HELPER FUNCTIONS
    /////////////////////////////////////////////////////////////// */

    /* 
    @notice Returns the onERC721Received selector.
    @dev Needed to receive ERC721 tokens.
    */
    function onERC721Received(address, address, uint256, bytes calldata) public pure returns (bytes4) {
        return this.onERC721Received.selector;
    }

    /*
    @notice Returns the onERC1155Received selector.
    @dev Needed to receive ERC1155 tokens.
    */
    function onERC1155Received(address, address, uint256, uint256, bytes calldata) public pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    /*
    @notice Called when function selector doesn't match any other.
    @dev No fallback allowed.
    */
    fallback() external {
        revert AccountErrors.NoFallback();
    }

    /*
    @notice Called on a plain ETH transfer.
    */
    receive() external payable { }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.4;

import {LibMulticaller} from

;

contract MasterBunni is IMasterBunni, ReentrancyGuard {
    using FixedPointMathLib for *;
    using SafeTransferLib for address;

    uint256 internal constant PRECISION = 1e36;
    uint256 internal constant REWARD_RATE_PRECISION = 1e6;
    uint256 internal constant PRECISION_DIV_REWARD_RATE_PRECISION = PRECISION / REWARD_RATE_PRECISION;

    mapping(address user => mapping(IERC20Lockable stakeToken => uint256)) public userPoolCounts;

    mapping(RushPoolId id => RushStakeState) public rushPoolStates;
    mapping(RushPoolId id => mapping(address incentiveToken => uint256)) public rushPoolIncentiveAmounts;
    mapping(RushPoolId id => mapping(address incentiveToken => mapping(address depositor => uint256))) public
        rushPoolIncentiveDeposits;
    mapping(RushPoolId id => mapping(address user => RushStakeState)) public rushPoolUserStates;
    mapping(RushPoolId id => mapping(address user => mapping(address incentiveToken => uint256))) public
        rushPoolUserRewardPaid;

    mapping(RecurPoolId id => RecurPoolState) public recurPoolStates;

    /// -----------------------------------------------------------------------
    /// Incentivizer actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function depositIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // record incentive in each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up incentive amount
            totalIncentiveAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // add incentive to pool
            rushPoolIncentiveAmounts[id][incentiveToken] += params[i].incentiveAmount;

            // add incentive to depositor
            rushPoolIncentiveDeposits[id][incentiveToken][recipient] += params[i].incentiveAmount;
        }

        // transfer incentive tokens to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit DepositIncentive(msgSender, incentiveToken, recipient, params, totalIncentiveAmount);
    }

    /// @inheritdoc IMasterBunni
    function withdrawIncentive(RushIncentiveParams[] calldata params, address incentiveToken, address recipient)
        external
        nonReentrant
        returns (uint256 totalWithdrawnAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        // subtract incentive tokens from each pool
        for (uint256 i; i < params.length; i++) {
            if (!isValidRushPoolKey(params[i].key) || block.timestamp >= params[i].key.startTimestamp) {
                // key is invalid or program is already active, skip
                continue;
            }

            // sum up withdrawn amount
            totalWithdrawnAmount += params[i].incentiveAmount;

            RushPoolId id = params[i].key.toId();

            // subtract incentive from pool
            rushPoolIncentiveAmounts[id][incentiveToken] -= params[i].incentiveAmount;

            // subtract incentive from sender
            rushPoolIncentiveDeposits[id][incentiveToken][msgSender] -= params[i].incentiveAmount;
        }

        // transfer incentive tokens to recipient
        if (totalWithdrawnAmount != 0) {
            incentiveToken.safeTransfer(recipient, totalWithdrawnAmount);
        }

        // emit event
        emit WithdrawIncentive(msgSender, incentiveToken, recipient, params, totalWithdrawnAmount);
    }

    /// @inheritdoc IMasterBunni
    function refundIncentive(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalRefundAmount;
            for (uint256 j; j < params[i].keys.length; j++) {
                // the program should be over
                RushPoolKey calldata key = params[i].keys[j];
                if (!isValidRushPoolKey(key) || block.timestamp <= key.startTimestamp + key.programLength) {
                    continue;
                }

                // load state
                RushPoolId id = key.toId();
                RushStakeState memory poolState = rushPoolStates[id];
                uint256 incentiveAmount = rushPoolIncentiveDeposits[id][incentiveToken][msgSender]; // the incentives added by msgSender
                if (incentiveAmount == 0) {
                    continue;
                }

                // compute refund amount
                // refund amount is the provided incentive amount minus the reward paid to stakers
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 refundAmount = incentiveAmount - rewardAccrued;

                // delete incentive deposit to mark the incentive as refunded
                delete rushPoolIncentiveDeposits[id][incentiveToken][msgSender];

                // accumulate refund amount
                totalRefundAmount += refundAmount;
            }

            // transfer refund amount to recipient
            if (totalRefundAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalRefundAmount);
            }

            // emit event
            emit RefundIncentive(msgSender, incentiveToken, recipient, totalRefundAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function incentivizeRecurPool(RecurIncentiveParams[] calldata params, address incentiveToken)
        external
        returns (uint256 totalIncentiveAmount)
    {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            if (params[i].incentiveAmount == 0) continue;

            RecurPoolKey calldata key = params[i].key;
            if (!isValidRecurPoolKey(key)) continue;

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint256 rewardRate = state.rewardRate;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = _rewardPerToken(
                state.rewardPerTokenStored, state.totalSupply, lastTimeRewardApplicable, lastUpdateTime, rewardRate
            );

            // record new reward
            uint256 newRewardRate;
            if (block.timestamp >= periodFinish) {
                // current period is over
                newRewardRate = params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, key.duration);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
                state.periodFinish = uint64(block.timestamp + key.duration);
            } else {
                // period is still active
                // add the new reward to the existing period
                uint256 remaining = periodFinish - block.timestamp;
                newRewardRate += params[i].incentiveAmount.mulDiv(REWARD_RATE_PRECISION, remaining);

                state.rewardRate = newRewardRate;
                state.lastUpdateTime = uint64(block.timestamp);
            }
            // prevent overflow when computing rewardPerToken
            if (newRewardRate >= ((type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION) / key.duration)) {
                revert MasterBunni__AmountTooLarge();
            }

            totalIncentiveAmount += params[i].incentiveAmount;
        }

        // transfer incentive tokens from msgSender to this contract
        if (totalIncentiveAmount != 0) {
            incentiveToken.safeTransferFrom2(msgSender, address(this), totalIncentiveAmount);
        }

        // emit event
        emit IncentivizeRecurPool(msgSender, incentiveToken, params, totalIncentiveAmount);
    }

    /// -----------------------------------------------------------------------
    /// Staker actions
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function joinRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // pool needs to be active
            if (
                !isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp
                    || block.timestamp > keys[i].startTimestamp + keys[i].programLength
            ) {
                continue;
            }

            // msgSender should be locked with address(this) as the unlocker
            if (
                !keys[i].stakeToken.isLocked(msgSender)
                    || keys[i].stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 remainderStakeAmount = poolState.stakeAmount - userState.stakeAmount; // stake in pool minus the user's existing stake
            uint256 stakeAmountUpdated;
            {
                uint256 balance = ERC20(address(keys[i].stakeToken)).balanceOf(msgSender);
                stakeAmountUpdated = remainderStakeAmount + balance > keys[i].stakeCap
                    ? keys[i].stakeCap - remainderStakeAmount
                    : balance;
            }

            // ensure there is capacity left and that we're increasing the user's stake
            // the user's stake may increase when either
            // 1) the user isn't staked yet or
            // 2) the user staked & hit the stake cap but more capacity has opened up since then
            if (stakeAmountUpdated == 0 || stakeAmountUpdated <= userState.stakeAmount) {
                continue;
            }

            // update user state
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: stakeAmountUpdated,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });
            if (userState.stakeAmount == 0) {
                // user didn't have any stake in this pool before
                unchecked {
                    ++userPoolCounts[msgSender][keys[i].stakeToken];
                }
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i],
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, keys[i].startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: remainderStakeAmount + stakeAmountUpdated,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRushPool(RushPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            // should be past pool's start timestamp
            if (!isValidRushPoolKey(keys[i]) || block.timestamp < keys[i].startTimestamp) {
                continue;
            }

            RushPoolId id = keys[i].toId();
            RushStakeState memory userState = rushPoolUserStates[id][msgSender];

            // user should have staked in the pool
            if (userState.stakeAmount == 0) {
                continue;
            }

            // update user state
            uint256 endTimestamp = keys[i].startTimestamp + keys[i].programLength;
            uint256 latestActiveTimestamp = FixedPointMathLib.min(block.timestamp, endTimestamp);
            uint256 userStakeXTimeUpdated = _computeStakeXTime(
                keys[i], userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
            );
            rushPoolUserStates[id][msgSender] = RushStakeState({
                stakeAmount: 0,
                stakeXTimeStored: userStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });
            unchecked {
                --userPoolCounts[msgSender][keys[i].stakeToken];
            }

            // update pool state
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 poolStakeXTimeUpdated = _computeStakeXTime(
                keys[i], poolState.stakeXTimeStored, poolState.stakeAmount, poolState.lastStakeAmountUpdateTimestamp
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount - userState.stakeAmount,
                stakeXTimeStored: poolStakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: latestActiveTimestamp
            });

            // emit event
            emit ExitRushPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function joinRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            // user should have non-zero balance
            uint256 balance = ERC20(address(key.stakeToken)).balanceOf(msgSender);
            if (balance == 0) {
                continue;
            }

            // user's balance should be locked with this contract as the unlocker
            if (
                !key.stakeToken.isLocked(msgSender)
                    || key.stakeToken.unlockerOf(msgSender) != IERC20Unlocker(address(this))
            ) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // can't stake in a pool twice
            if (balance <= stakedBalance) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply - stakedBalance + balance;
            state.balanceOf[msgSender] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function exitRecurPool(RecurPoolKey[] calldata keys) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < keys.length; i++) {
            RecurPoolKey calldata key = keys[i];

            /// -----------------------------------------------------------------------
            /// Validation
            /// -----------------------------------------------------------------------

            // key should be valid
            if (!isValidRecurPoolKey(key)) continue;

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[msgSender];

            // user should have staked in the pool
            if (stakedBalance == 0) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.rewards[msgSender] = _earned(
                state.userRewardPerTokenPaid[msgSender], stakedBalance, rewardPerTokenUpdated, state.rewards[msgSender]
            );
            state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

            // remove stake
            delete state.balanceOf[msgSender];
            // total supply has 1:1 relationship with staked amounts
            // so can't ever underflow
            unchecked {
                state.totalSupply = totalSupply - stakedBalance;
            }

            // decrement user pool count
            unchecked {
                --userPoolCounts[msgSender][key.stakeToken];
            }

            // emit event
            emit ExitRecurPool(msgSender, keys[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function unlock(IERC20Lockable[] calldata stakeTokens) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < stakeTokens.length; i++) {
            // pool count should be 0
            if (userPoolCounts[msgSender][stakeTokens[i]] != 0) {
                continue;
            }

            // address(this) should be the unlocker of msgSender
            // and msgSender should be locked
            if (
                stakeTokens[i].unlockerOf(msgSender) != IERC20Unlocker(address(this))
                    || !stakeTokens[i].isLocked(msgSender)
            ) {
                continue;
            }

            // unlock stake token
            stakeTokens[i].unlock(msgSender);

            // emit event
            emit Unlock(msgSender, stakeTokens[i]);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRushPool(RushClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RushPoolKey calldata key = params[i].keys[j];
                RushPoolId id = key.toId();

                // key should be valid
                if (!isValidRushPoolKey(key)) continue;

                // load state
                RushStakeState memory userState = rushPoolUserStates[id][msgSender];
                uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
                uint256 rewardPaid = rushPoolUserRewardPaid[id][msgSender][incentiveToken];

                // compute claimable reward
                uint256 stakeXTimeUpdated = _computeStakeXTime(
                    key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
                );
                uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
                uint256 claimableReward = rewardAccrued - rewardPaid;

                // update claim state
                rushPoolUserRewardPaid[id][msgSender][incentiveToken] = rewardAccrued;

                // accumulate claimable reward
                totalClaimableAmount += claimableReward;
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// @inheritdoc IMasterBunni
    function claimRecurPool(RecurClaimParams[] calldata params, address recipient) external nonReentrant {
        address msgSender = LibMulticaller.senderOrSigner();

        for (uint256 i; i < params.length; i++) {
            address incentiveToken = params[i].incentiveToken;
            uint256 totalClaimableAmount;

            for (uint256 j; j < params[i].keys.length; j++) {
                RecurPoolKey calldata key = params[i].keys[j];
                RecurPoolId id = key.toId();

                // key should be valid
                if (!isValidRecurPoolKey(key)) continue;

                /// -----------------------------------------------------------------------
                /// Storage loads
                /// -----------------------------------------------------------------------

                // load state
                RecurPoolState storage state = recurPoolStates[id];
                uint64 lastUpdateTime = state.lastUpdateTime;
                uint64 periodFinish = state.periodFinish;
                uint64 lastTimeRewardApplicable =
                    block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
                uint256 rewardPerTokenUpdated = _rewardPerToken(
                    state.rewardPerTokenStored,
                    state.totalSupply,
                    lastTimeRewardApplicable,
                    lastUpdateTime,
                    state.rewardRate
                );

                /// -----------------------------------------------------------------------
                /// State updates
                /// -----------------------------------------------------------------------

                // accrue rewards
                uint256 reward = _earned(
                    state.userRewardPerTokenPaid[msgSender],
                    state.balanceOf[msgSender],
                    rewardPerTokenUpdated,
                    state.rewards[msgSender]
                );
                state.rewardPerTokenStored = rewardPerTokenUpdated;
                state.lastUpdateTime = lastTimeRewardApplicable;
                state.userRewardPerTokenPaid[msgSender] = rewardPerTokenUpdated;

                if (reward != 0) {
                    // delete accrued rewards
                    delete state.rewards[msgSender];

                    // accumulate claimable amount
                    totalClaimableAmount += reward;
                }
            }

            // transfer incentive tokens to user
            if (totalClaimableAmount != 0) {
                incentiveToken.safeTransfer(recipient, totalClaimableAmount);
            }

            // emit event
            emit ClaimReward(msgSender, incentiveToken, recipient, totalClaimableAmount);
        }
    }

    /// -----------------------------------------------------------------------
    /// Getters
    /// -----------------------------------------------------------------------

    /// @inheritdoc IMasterBunni
    function getRushPoolClaimableReward(RushPoolKey calldata key, address user, address incentiveToken)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        // load state
        RushPoolId id = key.toId();
        RushStakeState memory userState = rushPoolUserStates[id][user];
        uint256 incentiveAmount = rushPoolIncentiveAmounts[id][incentiveToken];
        uint256 rewardPaid = rushPoolUserRewardPaid[id][user][incentiveToken];

        // compute claimable reward
        uint256 stakeXTimeUpdated = _computeStakeXTime(
            key, userState.stakeXTimeStored, userState.stakeAmount, userState.lastStakeAmountUpdateTimestamp
        );
        uint256 rewardAccrued = incentiveAmount.mulDiv(stakeXTimeUpdated, PRECISION);
        return rewardAccrued - rewardPaid;
    }

    /// @inheritdoc IMasterBunni
    function getRecurPoolClaimableReward(RecurPoolKey calldata key, address user)
        external
        view
        returns (uint256 claimableReward)
    {
        // no need to validate key since we just return 0 if it's invalid
        RecurPoolId id = key.toId();
        RecurPoolState storage state = recurPoolStates[id];
        uint64 periodFinish = state.periodFinish;
        uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
        uint256 rewardPerTokenUpdated = _rewardPerToken(
            state.rewardPerTokenStored,
            state.totalSupply,
            lastTimeRewardApplicable,
            state.lastUpdateTime,
            state.rewardRate
        );
        return _earned(
            state.userRewardPerTokenPaid[user], state.balanceOf[user], rewardPerTokenUpdated, state.rewards[ user]
        );
    }

    /// @inheritdoc IMasterBunni
    function recurPoolStakeBalanceOf(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].balanceOf[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolUserRewardPerTokenPaid(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].userRewardPerTokenPaid[user];
    }

    /// @inheritdoc IMasterBunni
    function recurPoolRewards(RecurPoolId id, address user) external view returns (uint256) {
        return recurPoolStates[id].rewards[user];
    }

    /// @inheritdoc IMasterBunni
    function isValidRushPoolKey(RushPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.stakeCap != 0 && key.startTimestamp != 0
            && key.programLength != 0;
    }

    /// @inheritdoc IMasterBunni
    function isValidRecurPoolKey(RecurPoolKey memory key) public pure returns (bool) {
        return address(key.stakeToken) != address(0) && key.rewardToken != address(0) && key.duration != 0;
    }

    /// -----------------------------------------------------------------------
    /// Callbacks
    /// -----------------------------------------------------------------------

    /// @inheritdoc IERC20Unlocker
    /// @dev Should initialize the user's stake position.
    function lockCallback(address account, uint256 balance, bytes calldata data) external nonReentrant {
        LockCallbackData memory callbackData = abi.decode(data, (LockCallbackData));
        IERC20Lockable stakeToken = IERC20Lockable(msg.sender);

        for (uint256 i; i < callbackData.rushKeys.length; i++) {
            RushPoolKey memory key = callbackData.rushKeys[i];
            uint256 endTimestamp = key.startTimestamp + key.programLength;

            // validate key
            // - key should be valid
            // - pool should be active
            // - stakeToken of key should be msg.sender
            if (
                !isValidRushPoolKey(key) || key.stakeToken != stakeToken || block.timestamp < key.startTimestamp
                    || block.timestamp > endTimestamp
            ) {
                continue;
            }

            RushPoolId id = key.toId();
            uint256 userStakeAmount = rushPoolUserStates[id][account].stakeAmount;
            // can't stake in a pool twice
            if (userStakeAmount != 0) {
                continue;
            }
            RushStakeState memory poolState = rushPoolStates[id];
            uint256 stakeAmount =
                poolState.stakeAmount + balance > key.stakeCap ? key.stakeCap - poolState.stakeAmount : balance;
            // ensure there is capacity left
            if (stakeAmount == 0) {
                continue;
            }

            // update user state
            // leave stakeXTime unchanged since stakeAmount was zero since the last update
            // block.timestamp is at most endTimestamp
            // since we already checked that the program is active
            rushPoolUserStates[id][account].stakeAmount = stakeAmount;
            rushPoolUserStates[id][account].lastStakeAmountUpdateTimestamp = block.timestamp;
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // update pool state
            // poolState.lastStakeAmountUpdateTimestamp might be 0 if the pool has never had stakers
            // so we bound it by the start timestamp of the program
            uint256 stakeXTimeUpdated = _computeStakeXTime(
                key,
                poolState.stakeXTimeStored,
                poolState.stakeAmount,
                FixedPointMathLib.max(poolState.lastStakeAmountUpdateTimestamp, key.startTimestamp)
            );
            rushPoolStates[id] = RushStakeState({
                stakeAmount: poolState.stakeAmount + stakeAmount,
                stakeXTimeStored: stakeXTimeUpdated,
                lastStakeAmountUpdateTimestamp: block.timestamp
            });

            // emit event
            emit JoinRushPool(account, key);
        }

        for (uint256 i; i < callbackData.recurKeys.length; i++) {
            RecurPoolKey memory key = callbackData.recurKeys[i];

            // validate key
            // - key should be valid
            // - stakeToken of key should be msg.sender
            if (!isValidRecurPoolKey(key) || key.stakeToken != stakeToken) {
                continue;
            }

            /// -----------------------------------------------------------------------
            /// Storage loads
            /// -----------------------------------------------------------------------

            RecurPoolId id = key.toId();
            RecurPoolState storage state = recurPoolStates[id];
            uint256 stakedBalance = state.balanceOf[account];

            // can't stake in a pool twice
            if (stakedBalance != 0) {
                continue;
            }

            uint64 lastUpdateTime = state.lastUpdateTime;
            uint64 periodFinish = state.periodFinish;
            uint64 lastTimeRewardApplicable = block.timestamp < periodFinish ? uint64(block.timestamp) : periodFinish;
            uint256 totalSupply = state.totalSupply;
            uint256 rewardPerTokenUpdated = _rewardPerToken(
                state.rewardPerTokenStored, totalSupply, lastTimeRewardApplicable, lastUpdateTime, state.rewardRate
            );

            /// -----------------------------------------------------------------------
            /// State updates
            /// -----------------------------------------------------------------------

            // accrue rewards
            // stakedBalance has been 0 so no need to update state.rewards[account]
            state.rewardPerTokenStored = rewardPerTokenUpdated;
            state.lastUpdateTime = lastTimeRewardApplicable;
            state.userRewardPerTokenPaid[account] = rewardPerTokenUpdated;

            // stake
            state.totalSupply = totalSupply + balance;
            state.balanceOf[account] = balance;

            // increment user pool count
            unchecked {
                ++userPoolCounts[account][key.stakeToken];
            }

            // emit event
            emit JoinRecurPool(account, key);
        }
    }

    /// @inheritdoc IERC20Unlocker
    function lockedUserReceiveCallback(address account, uint256 receiveAmount) external {}

    /// -----------------------------------------------------------------------
    /// Internal utilities
    /// -----------------------------------------------------------------------

    /// @dev Computes the updated (normalized stake amount) x (normalized time since program start) value. This value is useful
    /// since (stake x time) x (incentive amount) is the incentive amount accrued for the user / pool so far.
    /// Example: If a user has staked 0.5 x stakeCap tokens for 0.3 x programLength seconds, the stake x time value is 0.15 which is
    /// the proportion of the total incentive amount that the user has accrued so far.
    /// @param key The rush pool key.
    /// @param stakeXTimeStored The stake x time value stored in the state.
    /// @param stakeAmount The stake amount of the user between the last update and now.
    /// @param lastStakeAmountUpdateTimestamp The timestamp of the last update. Should be at most the end timestamp of the program.
    /// @return The updated stake x time value.
    function _computeStakeXTime(
        RushPoolKey memory key,
        uint256 stakeXTimeStored,
        uint256 stakeAmount,
        uint256 lastStakeAmountUpdateTimestamp
    ) internal view returns (uint256) {
        if (block.timestamp < key.startTimestamp) {
            return 0;
        }
        uint256 endTimestamp = key.startTimestamp + key.programLength;
        uint256 timeElapsedSinceLastUpdate =
            FixedPointMathLib.min(block.timestamp, endTimestamp) - lastStakeAmountUpdateTimestamp;
        return stakeXTimeStored
            + PRECISION.mulDiv(stakeAmount, key.stakeCap).mulDiv(timeElapsedSinceLastUpdate, key.programLength);
    }

    function _earned(
        uint256 userRewardPerTokenPaid,
        uint256 accountBalance,
        uint256 rewardPerToken,
        uint256 accountRewards
    ) internal pure returns (uint256) {
        return FixedPointMathLib.fullMulDiv(accountBalance, rewardPerToken - userRewardPerTokenPaid, PRECISION)
            + accountRewards;
    }

    function _rewardPerToken(
        uint256 rewardPerTokenStored,
        uint256 totalSupply,
        uint256 lastTimeRewardApplicable,
        uint256 lastUpdateTime,
        uint256 rewardRate
    ) internal pure returns (uint256) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }
        // mulDiv won't overflow since we check that rewardRate is less than (type(uint256).max / PRECISION_DIV_REWARD_RATE_PRECISION / duration)
        return rewardPerTokenStored
            + FixedPointMathLib.mulDiv(
                (lastTimeRewardApplicable - lastUpdateTime) * PRECISION_DIV_REWARD_RATE_PRECISION, rewardRate, totalSupply
            );
    }
}

